import React from 'react';
import './home.css'
import Axios from 'axios'
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from 'react-router-dom';
import Header from '../Header.js'
import { useNavigate } from "react-router-dom";

export default class Home extends React.Component{
 
    state ={
        id: 1,
        username: "",
        password: "",
        total: 0,
        loggedin: false
      }

      login(){
        var self = this;
        let username = document.getElementById("loguser").value;
        let password = document.getElementById("logpass").value; 
        if(username.length > 0 && password.length > 0){
            Axios.post("http://localhost:3001/api/login", {   
                username: username, 
                password: password,
            }).then(function(response){
                if (response.data.message){
                    console.log(response.data.message);
                }
                else{
                    console.log(response.data[0].id);
                    this.setState({id:1});
                    // self.setState({ id: response.data[0].id})
                    // self.setState({ username: response.data[0].username})
                    // self.setState({ password: response.data[0].password})
                    // self.setState({ total: response.data[0].total})
                    // self.setState({ loggedin: true})
                    // let navigate = useNavigate(); 
                    // let path = '/store'; 
                    // navigate(path);
                }
            })
        }
      }

      register(){
        let username = document.getElementById("reguser").value;
        let password = document.getElementById("regpass").value; 
        Axios.post("http://localhost:3001/api/register", {
            username: username, 
            password: password,
      })
    }

    render(){
    return(
        <div className="log-form">
            <h2 className="loginheader">Login to your account</h2>
            <div className="form">
                <input id = "loguser" type="text" title="username" placeholder="username" />
                <input id = "logpass" type="text" title="password" placeholder="password" />
                <button onClick={this.login} className="btn" type="submit">Login</button>
            </div>
            <h2 className="loginheader">Register new account</h2>
            <div className="form">
                <input id = "reguser" type="text" title="username" placeholder="username" />
                <input id = "regpass" type="text" title="password" placeholder="password" />
                <button onClick={this.register} className="btn" type="submit">Register</button>
            </div>
        </div>
        )}
    }
