import React from 'react';
import './home.css'
import Axios from 'axios'
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from 'react-router-dom';
import Header from '../Header.js'
import { useNavigate } from "react-router-dom";

export default function Home(){
 
    let navigate = useNavigate();

        const idu = 1;
        const usernameu = "";
        const passwordu = "";
        const totalu = 0;
        const loggedin = false;

      const login = () => {
        // var self = this;
        let username = document.getElementById("loguser").value;
        let password = document.getElementById("logpass").value; 
        if(username.length > 0 && password.length > 0){
            Axios.post("http://localhost:3001/api/login", {   
                username: username, 
                password: password,
            }).then(function(response){
                if (response.data.message){
                    console.log(response.data.message);
                }
                else{
                    loggedin = true;
                    idu = response.data[0].id
                    usernameu = response.data[0].username
                    passwordu = response.data[0].password
                    totalu = response.data[0].total
                    // navigate('/store');
                }
            })
        }
      }

     const register = () => {
        let username = document.getElementById("reguser").value;
        let password = document.getElementById("regpass").value; 
        Axios.post("http://localhost:3001/api/register", {
            username: username, 
            password: password,
      })
    }

    return(
        <div className="log-form">
            <h2 className="loginheader">Login to your account</h2>
            <div className="form">
                <input id = "loguser" type="text" title="username" placeholder="username" />
                <input id = "logpass" type="text" title="password" placeholder="password" />
                <button onClick={login()} className="btn" type="submit">Login</button>
            </div>
            <h2 className="loginheader">Register new account</h2>
            <div className="form">
                <input id = "reguser" type="text" title="username" placeholder="username" />
                <input id = "regpass" type="text" title="password" placeholder="password" />
                <button onClick={register()} className="btn" type="submit">Register</button>
            </div>
            <h2>{loggedin}</h2>
        </div>
    )
}
