import React from 'react';
import './home.css'
import Axios from 'axios'
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from 'react-router-dom';
import Header from '../Header.js'
import { useNavigate } from "react-router-dom";

export default class Home extends React.Component{
 
    state ={
        id: 1,
        username: "",
        password: "",
        total: 0,
        loggedin: false
      }

      login(){
        var self = this;
        let username = document.getElementById("loguser").value;
        let password = document.getElementById("logpass").value; 
        Axios.post("http://localhost:3001/api/login", {   
            username: username, 
            password: password,
        }).then(function(response){
            self.setState({ id: response.data.id})
            self.setState({ username: response.data.username})
            self.setState({ password: response.data.password})
            self.setState({ total: response.data.total})
            self.setState({ loggedin: true})
            let navigate = useNavigate(); 
            let path = '/store'; 
            console.log('asdf')
            navigate(path);
        })
      }

      register(){
        let username = document.getElementById("reguser").value;
        let password = document.getElementById("regpass").value; 
        Axios.post("http://localhost:3001/api/register", {
            username: username, 
            password: password,
      })
    }

    render(){
    return(
        <div className="log-form">
            <h2 className="loginheader">Login to your account</h2>
            <form>
                <input id = "loguser" type="text" title="username" placeholder="username" />
                <input id = "logpass" type="text" title="password" placeholder="password" />
                <button onClick={this.login} className="btn" type="submit">Login</button>
            </form>
            <h2 className="loginheader">Register new account</h2>
            <form>
                <input id = "reguser" type="text" title="username" placeholder="username" />
                <input id = "regpass" type="text" title="password" placeholder="password" />
                <button onClick={this.register} className="btn" type="submit">Register</button>
            </form>
        </div>
        )}
    }
